mapper.select:
SELECT tbl.* FROM ( SELECT id , title , writer , content , start_date , end_date , write_datetime , modified_datetime FROM post
ORDER BY start_date DESC
ORDER BY start_date ASC
) tbl
LIMIT ${start}, ${count}

mapper.insert:
              INSERT INTO post (
                                title
                              , writer
   , content
, start_date
  , end_date
                              , write_datetime
                              , modified_datetime
                              ) VALUES (
                                #{title}
                              , #{writer}
   , #{content}
, #{start_date}
  , #{end_date}
                              , now()
                              , now()
                              )
